#include <iostream>
#include <string>
using namespace std;

string reverseString(string s) {
    int left = 0, right = s.length() - 1;
    while (left < right) {
        swap(s[left], s[right]);
        left++;
        right--;
    }
    return s;
}

int main() {
    string s;
    getline(cin, s);
    string result = reverseString(s);
    cout << result << endl;
    return 0;
}
